  this.tools = {};
}

EditorConfig.prototype.setConfig = function (data) {
  this.tools = data.tools || {};
  this.dialogue = data.dialogue || [];
  this.maxEmoticon = data.maxEmoticon || [];
  this.minEmoticon = data.minEmoticon || [];
  this.uploadImg = data.uploadImg
    ? async (file, src) => {
        try {
          let result = await data.uploadImg(file, src);
          return [undefined, result];
        } catch (err) {
          return [err, undefined];
        }
      }
    : null;
};

export default new EditorConfig();
