function Utils() {
  this.createUid = (() => {
    let regxy = /[xy]/g;
    let reg = /-/g;
    let empty = "";
    return () => {
      let uuid = "xxxxxxxx-xxxx-xxxx-yxxx-xxxxxxxxxxxx".replace(
        regxy,
        function (c) {
          var r = (Math.random() * 16) | 0,
            v = c == "x" ? r : (r & 0x3) | 0x8;
          return v.toString(16);
        }
      );
      return uuid.replace(reg, empty);
    };
  })();
}

export default new Utils();
