<template>
  <div class="dropDown-select">
    <div class="dropDown-select-item">
      <div class="dropDown-select-head" @click="open">
        <slot name="title">
          <div class="dropDown-select-title">
            {{ title }}
          </div>
        </slot>
      </div>
      <div
        @click.stop
        :class="[
          'dropDown-select-content',
          { 'dropDown-select-content-open': value },
        ]"
      >
        <slot>
          <div class="box"></div>
        </slot>
      </div>
    </div>
  </div>
</template>

<script>
import $publish from "../config/Publish";
export default {
  name: "WorkspaceJsonDropDownSelect",
  mode: {
    prop: "value",
    event: "input",
  },
  props: {
    title: {
      default: "标题",
    },
    value: {
      default: false,
    },
  },
  data() {
    return {};
  },
  mounted() {
    $publish.on("initOpenState", () => {
      this.$emit("input", false);
    });
  },
  methods: {
    open() {
      setTimeout(() => this.$emit("input", !this.value));
    },
  },
};
</script>

<style lang="scss" scoped>
.dropDown-select {
  // width: 100%;
}
.dropDown-select-item {
  width: 100%;
  font-size: 12px;
  position: relative;
}

.dropDown-select-item .dropDown-select-title {
  width: 100%;
  height: 25px;
  line-height: 25px;
  text-align: center;
  cursor: pointer;
  background: #f5f5f5;
}

.dropDown-select-item .dropDown-select-content {
  position: absolute;
  left: 50%;
  transition: all 0.2s;
  transform: scale(1, 0.5) translateX(-0%) translateX(-50%);
  transform-origin: top;
  opacity: 0;
  word-break: break-all;
  pointer-events: none;
  z-index: 999999;
}
.dropDown-select-item .dropDown-select-content-open {
  transform: scale(1, 1) translateX(-0%) translateX(-50%);
  opacity: 1;
  pointer-events: auto;
}

.dropDown-select-item .box {
  width: 100px;
  height: 100px;
  background: #fff;
}
</style>
