<template>
  <div>
    <div class="extend-content">
      <textarea
        placeholder="B站/网易云音乐外链/嵌入代码"
        name=""
        id=""
        cols="30"
        rows="10"
        v-model="content"
        @change="onTextarea"
      ></textarea>
      <div class="extend-btn">
        <div @click="confirm" class="extend-confirm">确认</div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "WorkspaceJsonExtendContent",
  mode: {
    prop: "value",
  },
  props: {
    value: {
      default: "",
    },
  },
  data() {
    return {
      content: "",
    };
  },
  mounted() {},
  watch: {
    value: {
      immediate: true,
      handler() {
        this.content = this.value;
      },
    },
  },
  methods: {
    onTextarea() {
      this.$emit("input", this.content);
    },
    confirm() {
      if (this.content) {
        this.$emit("confirm", this.content);
      }
    },
  },
};
</script>

