<template>
  <div>
    <div class="link-bar-content">
      <div class="link-bar-item">
        <span>名称</span
        ><input v-model="title" type="text" placeholder="链接名称" />
      </div>
      <div class="link-bar-item">
        <span>链接</span
        ><input v-model="src" type="text" placeholder="链接地址" />
      </div>
      <div @click="confirm" class="link-bar-button">确认</div>
    </div>
  </div>
</template>

<script>
export default {
  name: "WorkspaceJsonLinkBarContent",
  props: {
    name: {
      default: "",
    },
    link: {
      default: "",
    },
    autoClear: {
      default: true,
    },
  },
  data() {
    return {
      title: "",
      src: "",
    };
  },
  mounted() {},
  watch: {
    name: {
      immediate: true,
      handler() {
        this.title = this.name;
      },
    },
    link: {
      immediate: true,
      handler() {
        this.src = this.link;
      },
    },
  },
  methods: {
    confirm() {
      if (this.title && this.src) {
        this.$emit("confirm", {
          name: this.title,
          link: this.src,
        });
        if (this.autoClear) {
          this.title = "";
          this.src = "";
        }
      }
    },
  },
};
</script>

