<template>
  <div>
    <div :class="['select-color', { 'select-color-show': index == current }]">
      <span class="item" v-for="(item, index) in colorLists" :key="index">
        <span
          class="color-item"
          @click.stop="selectColor(item)"
          :style="{
            background: item.background,
            border: `1px solid ${item.color}`,
            ...styles,
          }"
        ></span>
      </span>
    </div>
  </div>
</template>

<script>
export default {
  name: "CodeSelectColor",
  props: {
    index: {
      default: 0,
    },
    current: {
      default: null,
    },
    styles: {
      default() {
        return {};
      },
    },
  },
  data() {
    return {
      colorList: [
        {
          color: "#e7e9e8",
          background: "#f5f5f5",
        },
        {
          color: "#c1e77e",
          background: "rgba(237, 248, 219, 1)",
        },
        {
          color: "#f1a2ab",
          background: "rgba(248, 206, 211, 0.5)",
        },
        {
          color: "#f5d480",
          background: "rgba(246, 225, 172, 0.5)",
        },
        {
          color: "#1e80ff",
          background: "rgba(192, 221, 252, 0.5)",
        },
        {
          color: "#ba9bf2",
          background: "rgba(217, 201, 248, 0.5)",
        },
      ],
    };
  },

  mounted() {},
  computed: {
    colorLists() {
      return this.colorList.splice(1);
    },
  },
  methods: {
    selectColor(item) {
      this.$emit("change", item);
    },
  },
};
</script>

<style lang="scss" scoped>
.select-color {
  position: absolute;
  left: 50%;
  bottom: -3px;
  transform: translate(-50%, 100%);
  display: none;
  cursor: pointer;
  z-index: 99;
  background: #fff;
  box-shadow: 0 5px 10px 0 #e4e8eb;
}
.select-color-show {
  display: flex;
}
.select-color .item {
  padding: 4px 1px;
  display: flex;
}
.select-color .item:hover {
  background: #eee;
}
.select-color .color-item {
  width: 20px;
  height: 20px;
  border: 1px solid transparent;
  margin: 0 2px;
  border-radius: 2px;
  display: inline-block;
}
</style>
