<template>
  <div class="align-mode" @mousedown.prevent>
    <dropdown-select v-model="isOpen">
      <!-- 展示 -->
      <div slot="title">
        <div class="head">
          <!-- 对齐方式 -->
          <i class="iconfont icon-zuoyouduiqi"></i>
        </div>
      </div>
      <!-- 操作 -->
      <div class="dropdown-panel">
        <p @click="execCommand('justifyLeft')">左对齐</p>
        <p @click="execCommand('justifyCenter')">居中</p>
        <p @click="execCommand('justifyRight')">右对齐</p>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../../components/dropdown-select.vue";
export default {
  name: "WorkspaceJsonAlignMode",
  components: {
    dropdownSelect,
  },
  data() {
    return {
      isOpen: false,
    };
  },

  mounted() {},

  methods: {
    execCommand(type) {
      document.execCommand(type, false, null);
      this.isOpen = false;
    },
  },
};
</script>

