<template>
  <div class="emote-symbol" @mousedown.prevent>
    <dropdown-select v-model="isOpen">
      <!-- 展示 -->
      <div slot="title">
        <div class="head">
          <!-- 表情符号 -->
          <i class="iconfont icon-a-1"></i>
        </div>
      </div>
      <!-- 操作 -->
      <div class="dropdown-panel">
        <div class="list">
          <div
            @click="execCommand(item)"
            v-for="(item, index) in currentList"
            :key="index"
            class="emote-item"
          >
            {{ item }}
          </div>
        </div>

        <div class="tabs" :style="{ '--theme': config.themeColor }">
          <span
            @click="switchTabs(item, index)"
            :class="[{ active: index == checked }]"
            v-for="(item, index) in emoticonList"
            :key="index"
            >{{ item.title }}</span
          >
        </div>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../../components/dropdown-select.vue";
import config from "../../config/index";
import emoteSymbol from "../utils/emoteSymbol";
export default {
  name: "WorkspaceJsonAlignMode",
  components: {
    dropdownSelect,
  },
  data() {
    return {
      isOpen: false,
      checked: 0,
      currentList: [],
      emoticonList: emoteSymbol,
      config,
    };
  },
  mounted() {
    this.currentList = emoteSymbol[0].list;
  },
  methods: {
    switchTabs(item, index) {
      this.checked = index;
      this.currentList = item.list;
    },
    execCommand(item) {
      document.execCommand(
        "insertHTML",
        false,
        `<a style="font-size: 22px;line-height:16px;position: relative;top: 1px">${item}</a><span>&nbsp</span>`
      );
    },
  },
};
</script>

