<template>
  <div class="emoticon" @mousedown.prevent>
    <dropdown-select v-model="isOpen">
      <!-- 展示 -->
      <div slot="title">
        <div class="head">
          <!-- 表情 -->
          <i class="iconfont icon-biaoqing-xue"></i>
        </div>
      </div>
      <!-- 操作 -->
      <div class="dropdown-panel">
        <div v-if="load" class="list">
          <div
            @click="execCommand(item)"
            v-for="(item, index) in currentList"
            :key="index"
            class="img-item"
          >
            <img v-lazy-load="item.src" alt="" />
          </div>
        </div>
        <div class="tabs" :style="{ '--theme': config.themeColor }">
          <span
            @click="switchTabs(item, index)"
            :class="[{ active: index == checked }]"
            v-for="(item, index) in emoticonList"
            :key="index"
            >{{ item.title }}</span
          >
        </div>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../../components/dropdown-select.vue";
import config from "../../config/index";
import lazyLoad from "../../directives/lazy-load";

export default {
  name: "WorkspaceJsonAlignMode",
  components: {
    dropdownSelect,
  },
  directives: {
    lazyLoad,
  },
  data() {
    return {
      isOpen: false,
      load: false,
      checked: 0,
      config,
    };
  },
  computed: {
    emoticonList() {
      return config.minEmoticon;
    },
    currentList() {
      return config.minEmoticon[this.checked]
        ? config.minEmoticon[this.checked].list
        : [];
    },
  },
  watch: {
    isOpen() {
      if (this.isOpen) this.load = true;
    },
  },

  methods: {
    switchTabs(item, index) {
      this.checked = index;
    },
    execCommand(item) {
      document.execCommand(
        "insertHTML",
        false,
        `<img class="laytEditor-emoticon" style="width:21px;height:21px;position: relative;top: 3px;" src="${item.src}" alt="">&nbsp`
      );
    },
  },
};
</script>

