<template>
  <div class="link">
    <dropdown-select v-model="isOpen">
      <!-- 展示 -->
      <div slot="title">
        <div class="head">
          <!-- 链接 -->
          <i class="iconfont icon-lianjie"></i>
        </div>
      </div>
      <!-- 操作 -->
      <div class="dropdown-panel">
        <linkbar-content @confirm="confirm"> </linkbar-content>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import linkbarContent from "../../components/linkbar-content.vue";
import dropdownSelect from "../../components/dropdown-select.vue";
export default {
  name: "WorkspaceJsonAlignMode",
  components: {
    dropdownSelect,
    linkbarContent,
  },

  props: {
    range: {
      default: null,
    },
  },
  data() {
    return {
      isOpen: false,
    };
  },

  mounted() {},

  methods: {
    confirm(data) {
      this.restoreSelection();
      document.execCommand(
        "insertHTML",
        false,
        `<a style="color:rgb(193, 231, 126)" target="_blank"  href="${data.link}">${data.name}</a>`
      );
      this.isOpen = false;
    },
    // 设置光标位置
    restoreSelection() {
      let currentRange = this.range;
      if (!currentRange) {
        return;
      }
      var supportRange = typeof document.createRange === "function";
      let selection = document.getSelection(),
        range;
      if (supportRange) {
        selection = document.getSelection();
        selection.removeAllRanges();
        selection.addRange(currentRange);
      } else {
        range = document.selection.createRange();
        range.setEndPoint("EndToEnd", currentRange);
        if (currentRange.text.length === 0) {
          range.collapse(false);
        } else {
          range.setEndPoint("StartToStart", currentRange);
        }
        range.select();
      }
    },
  },
};
</script>
