<template>
  <div class="text-bold">
    <div
      :class="['head', { 'head-active': isChecked }]"
      @mousedown.prevent
      @click="execCommand()"
    >
      <!-- 加粗 -->
      <i class="iconfont icon-bold"></i>
    </div>
  </div>
</template>

<script>
export default {
  name: "WorkspaceJsonAlignMode",
  components: {},
  props: {
    state: {
      default: [],
    },
  },
  data() {
    return {
      command: "bold",
    };
  },
  computed: {
    isChecked() {
      return this.state.includes(this.command);
    },
  },

  mounted() {},

  methods: {
    execCommand() {
      document.execCommand(this.command, false, null);
      this.$emit("click", this.command);
    },
  },
};
</script>

<style lang="scss" scoped>
.text-bold .head {
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.text-bold .head:hover {
  background: #f5f5f5;
}

.text-bold .head-active {
  background: #eee;
  cursor: pointer;

}
</style>
