<template>
  <div class="text-bg">
    <dropdown-select v-model="isOpen">
      <!-- 展示 -->
      <div slot="title">
        <div class="head" @mousedown.prevent>
          <!-- 字体颜色 -->
          <i class="iconfont icon-zitiyanse"></i>
        </div>
      </div>
      <!-- 操作 -->
      <div @mousedown.prevent class="dropdown-panel">
        <span
          v-for="(item, index) in colorList"
          :key="index"
          @click="execCommand(item)"
          :style="{
            color: item,
          }"
          >A</span
        >
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../../components/dropdown-select.vue";
export default {
  name: "WorkspaceJsonAlignMode",
  components: {
    dropdownSelect,
  },
  data() {
    return {
      colorList: [
        "#67c23a",
        "#e6a23c",
        "#f56c6c",
        "#409eff",
        "#000000",
        "#303133",
        "#606266",
        "#909399",
      ],
      isOpen: false,
    };
  },

  mounted() {},

  methods: {
    execCommand(color) {
      document.execCommand("foreColor", false, color);
      this.isOpen = false;
    },
  },
};
</script>

