        <div class="head">
          <!-- 标签 -->
          <i class="iconfont icon-biaoqian"></i>
        </div>
      </div>
      <!-- 操作 -->
      <div class="dropdown-panel">
        <div
          @click="execCommand(item)"
          v-for="(item, index) in tags"
          :key="index"
        >
          <span :style="{ background: item.bg, color: item.color }">XXX</span>
        </div>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../../components/dropdown-select.vue";
export default {
  name: "WorkspaceJsonAlignMode",
  components: {
    dropdownSelect,
  },
  props: {
    range: {
      default() {
        return {};
      },
    },
  },
  data() {
    return {
      tags: [
        {
          color: "#999",
          bg: "#eff0f0",
        },
        {
          bg: "#f0f9eb",
          color: "#67c23a",
        },
        {
          bg: "#fef0f0",
          color: "#f56c6c",
        },
        {
          bg: "#fdf6ec",
          color: "#e6a23c",
        },

        {
          bg: "#ecf5ff",
          color: "#409eff",
        },
        {
          color: "#ba9bf2",
          bg: "rgba(217, 201, 248, 0.5)",
        },
      ],
      isOpen: false,
    };
  },
  mounted() {},
  methods: {
    execCommand(item) {
      let text = window.getSelection().toString();
      text = text || "标签";

      let family = `system, -apple-system, BlinkMacSystemFont, PingFang SC, Segoe UI,
          Microsoft YaHei, wenquanyi micro hei, Hiragino Sans GB, Hiragino Sans GB W3,
          Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
          Helvetica, Arial, sans-serif`;

      document.execCommand(
        "insertHTML",
        false,
        `<span style="background:${item.bg};color:${item.color};padding:2px 0px;border-radius:2px;font-size:1em;font-family:${family};word-break:break-all;">&nbsp${text}&nbsp</span>`
      );
      // document.execCommand("insertHTML", false, "<span>&nbsp</span>");
      // document.execCommand("insertText", false, text);
      // document.execCommand("insertHTML", false, "&nbsp");
    },
  },
};
</script>

