<template>
  <div class="underline">
    <div
      @mousedown.prevent
      @click="execCommand()"
      :class="['head', { 'head-active': isChecked }]"
    >
      <!-- 下划线 -->
      <i class="iconfont icon-fuwenbenbianjiqi_zitiyanse"></i>
    </div>
  </div>
</template>

<script>
export default {
  name: "WorkspaceJsonAlignMode",
  components: {},
  props: {
    state: {
      default: [],
    },
  },
  data() {
    return {
      command: "underline",
    };
  },
  computed: {
    isChecked() {
      return this.state.includes(this.command);
    },
  },
  mounted() {},

  methods: {
    execCommand() {
      document.execCommand(this.command, false, null);
      this.$emit("click", this.command);
    },
  },
};
</script>

<style lang="scss" scoped>
.underline .head {
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.underline .head:hover {
  background: #f5f5f5;
}

.underline .head-active {
  background: #eee;
}
</style>
