<template>
  <div class="code-block">
    <div class="code-block-section">
      <codemirror
        @blur="$emit('blur')"
        @focus="$emit('focus')"
        @change="onCode"
        v-model="value"
        :lang="lang"
      ></codemirror>
    </div>
  </div>
</template>

<script>
import codemirror from "../../components/codemirror/codemirror.vue";
// const hljs = require("highlight.js");

export default {
  name: "code-block",
  components: {
    codemirror,
  },
  props: {
    content: {
      default() {
        return {};
      },
    },
    // 类型
    token: {
      default: "",
    },
  },
  data() {
    return {
      value: "",
    };
  },
  computed: {
    lang() {
      return this.content.lang || "javascript";
    },
  },
  watch: {
    token: {
      immediate: true,
      handler() {
        this.$nextTick(() => {
          // console.log(this.content.html,'this.content.html');
          if (this.content.html) this.initData();
        });
      },
    },
  },
  mounted() {},
  methods: {
    initData() {
      let div = document.createElement("div");
      div.style.opacity = 0;
      div.style.position = "fixed";
      div.style.top = "-199999px";
      div.innerHTML = this.content.html;
      document.querySelector("body").appendChild(div);
      this.value = div.querySelector(".code-main").innerText;
      // innerText属性在获取文本内容时，会将空格、制表符、换行符等空白字符转换为特定格式的实体字符
      // 避免出现多余的"•"字符串
      this.value = this.value.replaceAll("​", " ");
      div.remove(div);
      this.onCode(this.content.html, this.lang, this.content.text);
    },
    onCode(html, lang, data) {
      this.$emit("change", {
        html: html,
        text: "",
        lang: lang,
      });
    },
  },
};
</script>

<style lang="scss" scoped>
.code-block-section {
  margin: 15px 20px;
  // background: #fff;
}
</style>
