<template>
  <div class="emoticon-card">
    <div v-html="html"></div>
  </div>
</template>
<script>
export default {
  name: "WorkspaceJsonEmoticon",
  props: {
    content: {
      default() {
        return {};
      },
    },
    token: {
      default: "",
    },
  },
  data() {
    return {
      html: "",
    };
  },
  watch: {
    token: {
      immediate: true,
      handler() {
        this.src = this.content.src;
        this.html = this.content.template;
        this.$emit("change", {
          src: this.src,
          template: this.html,
        });
      },
    },
  },
  mounted() {},
  methods: {},
};
</script>

