<template>
  <div class="default-text">
    <rich-text
      @change="onHandler"
      @commandState="onCommandState"
      v-if="themeType == 'text'"
    ></rich-text>
    <quote-temp
      @change="onHandler"
      @commandState="onCommandState"
      v-if="themeType == 'quote'"
    ></quote-temp>
    <state-temp
      @change="onHandler"
      @commandState="onCommandState"
      v-if="themeType == 'state'"
    ></state-temp>
    <colorblock-temp
      @change="onHandler"
      @commandState="onCommandState"
      v-if="themeType == 'colorblock'"
    ></colorblock-temp>
  </div>
</template>

<script>
import richText from "./components/richText.vue";
import quoteTemp from "./components/quoteTemp.vue";
import stateTemp from "./components/stateTemp.vue";
import colorblockTemp from "./components/colorblockTemp.vue";
export default {
  name: "default-text",
  components: {
    richText,
    quoteTemp,
    stateTemp,
    colorblockTemp,
  },
  props: {
    // ç±»åž‹
    themeType: {
      default: "",
    },
    token: {
      default: "",
    },
    content: {
      default() {
        return {};
      },
    },
    index: {
      default: 0,
    },
    current: {
      default: null,
    },
  },
  provide() {
    return {
      parent: this,
    };
  },
  data() {
    return {};
  },
  mounted() {},
  watch: {},

  methods: {
    onHandler(content) {
      this.$emit("change", content);
    },
    onCommandState(commandState) {
      this.$emit("commandState", commandState);
    },
  },
};
</script>

