<template>
  <div class="code-bar">
    <dropdown-select v-model="show" title="代码">
      <div class="code-bar-content">
        <p
          v-for="(item, index) in langs"
          :key="index"
          @click="selectLang(item)"
        >
          {{ item.name }}
        </p>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import langs from "../components/codemirror/langs/index";
import dropdownSelect from "../components/dropdown-select";
export default {
  name: "workspaceJsonTitle",
  components: {
    dropdownSelect,
  },
  data() {
    return {
      langs: langs,
      show: false,
    };
  },
  mounted() {},
  methods: {
    selectLang(item) {
      this.$emit("change", {
        type: "code",
        content: {
          html: "",
          text: "",
          lang: item.name,
        },
      });
      this.show = false;
    },
  },
};
</script>

