<template>
  <div class="extend-bar">
    <dropdown-select v-model="show" title="表情包">
      <div class="emoticon">
        <div v-if="isLoad" class="emoticon-list">
          <div
            class="item"
            @click="selectEmoticon(item)"
            v-for="(item, index) in semoticonList"
            :key="index"
          >
            <img v-lazy-load="item.src" alt="" />
          </div>
        </div>
      </div>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../components/dropdown-select";
import extendContent from "../components/extend-content";
import config from "../config/index";
import lazyLoad from "../directives/lazy-load";

const temp = (src) => {
  return `
    <div class="laytEditor-emoticon" style="display: flex; justify-content: center;padding:20px 0;">
      <div
        class="laytEditor-emoticon-img"
        style="
          max-width: 120px;
          max-height: 120px;
          min-width: 60px;
          min-height: 60px;
          background: #f5f5f5;
          padding: 10px;
          display: flex;
          border-radius:3px;
        "
      >
        <img
          style="max-width: 100%; max-height: 100%; -webkit-user-drag: none"
          src="${src}"
          alt=""
        />
      </div>
    </div>
    `;
};
export default {
  name: "WorkspaceJsonTitle",
  components: {
    dropdownSelect,
    extendContent,
  },
  directives: {
    lazyLoad,
  },
  data() {
    return {
      value: "",
      show: false,
      isLoad: false,
    };
  },

  computed: {
    semoticonList() {
      return config.maxEmoticon;
    },
  },
  watch: {
    show() {
      if (this.show) {
        this.$nextTick(() => {
          this.isLoad = true;
        });
        setTimeout(() => (this.isLoad = true), 100);
      }
    },
  },

  methods: {
    selectEmoticon(item) {
      this.$emit("change", {
        type: "emoticon",
        content: {
          src: item.src,
          template: temp(item.src),
        },
      });
      this.show = false;
    },
  },
};
</script>

