<template>
  <div class="extend-bar">
    <dropdown-select v-model="show" title="扩展">
      <extend-content v-model="value" @confirm="confirm"></extend-content>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../components/dropdown-select";
import extendContent from "../components/extend-content";
export default {
  name: "WorkspaceJsonTitle",
  components: {
    dropdownSelect,
    extendContent,
  },
  data() {
    return {
      value: "",
      show: false,
    };
  },
  mounted() {},

  methods: {
    confirm() {
      if (this.value) {
        this.$emit("change", {
          type: "extend",
          content: {
            text:this.value,
            template: "",
          },
        });
        this.value = "";
        this.show = false;
      }
    },
  },
};
</script>

