<template>
  <div class="link-bar">
    <dropdown-select v-model="show" title="链接">
      <linkbar-content @confirm="confirm"> </linkbar-content>
    </dropdown-select>
  </div>
</template>

<script>
import dropdownSelect from "../components/dropdown-select";
import linkbarContent from "../components/linkbar-content.vue";
export default {
  name: "WorkspaceJsonTitle",
  components: {
    dropdownSelect,
    linkbarContent,
  },
  data() {
    return {
      show: false,
    };
  },
  mounted() {},
  methods: {
    confirm(data) {
      this.$emit("change", {
        type: "link",
        content: {
          name: data.name,
          link: data.link,
          template: "",
        },
      });
      this.show = false;
    },
  },
};
</script>

